<?php require_once('../Connections/local.php'); ?><?php// ** Logout the current user. **$logoutAction = $_SERVER['PHP_SELF']."?doLogout=true";if ((isset($_SERVER['QUERY_STRING'])) && ($_SERVER['QUERY_STRING'] != "")){  $logoutAction .="&". htmlentities($_SERVER['QUERY_STRING']);}if ((isset($_GET['doLogout'])) &&($_GET['doLogout']=="true")){  //to fully log out a visitor we need to clear the session varialbles  $_SESSION['MM_Username'] = NULL;  $_SESSION['MM_UserGroup'] = NULL;  $_SESSION['PrevUrl'] = NULL;  unset($_SESSION['MM_Username']);  unset($_SESSION['MM_UserGroup']);  unset($_SESSION['PrevUrl']);	  $logoutGoTo = "../logout.php";  if ($logoutGoTo) {    header("Location: $logoutGoTo");    exit;  }}?><?phpif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}$editFormAction = $_SERVER['PHP_SELF'];if (isset($_SERVER['QUERY_STRING'])) {  $editFormAction .= "?" . htmlentities($_SERVER['QUERY_STRING']);}if ((isset($_POST["MM_insert"])) && ($_POST["MM_insert"] == "regi")) {  $nama = $_POST['Nama'];  $tahun = $_POST['Tahun'];  $no_matrik = $_POST['No_Matrik'];  $kata_laluan = $_POST['kata_laluan'];  mysql_select_db($database_local, $local);  // Check if the same data already exists in the table  $checkQuery = sprintf("SELECT * FROM pelajar WHERE nama = %s AND tahun = %s AND no_matrik = %s",    GetSQLValueString($nama, "text"),    GetSQLValueString($tahun, "int"),    GetSQLValueString($no_matrik, "text"));  $checkResult = mysql_query($checkQuery, $local) or die(mysql_error());  if (mysql_num_rows($checkResult) > 0) {    // Data already exists, show alert    echo '<script>alert("Data Pelajar Telah Wujud")</script>';  } else {    // Data doesn't exist, insert into database    $insertSQL = sprintf("INSERT INTO pelajar (nama, tahun, no_matrik, kata_laluan) VALUES (%s, %s, %s, %s)",                         GetSQLValueString($nama, "text"),                         GetSQLValueString($tahun, "int"),                         GetSQLValueString($no_matrik, "text"),                         GetSQLValueString($kata_laluan, "text"));    $Result1 = mysql_query($insertSQL, $local) or die(mysql_error());    // Show success message    echo '<script>alert("Pelajar Telah Berjaya Dimasukkan ke Rekod Pelajar")</script>';  }  $insertGoTo = "daftarpelajar.php";  if (isset($_SERVER['QUERY_STRING'])) {    $insertGoTo .= (strpos($insertGoTo, '?')) ? "&" : "?";    $insertGoTo .= $_SERVER['QUERY_STRING'];  }}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><script type = "text/javascript" src="../script.js"></script><link rel="stylesheet" href="../css/sidebar.css"><link rel="stylesheet" href="../css/header.css"><link rel="stylesheet" href="../css/register.css"><title>Sistem Kehadiran Pelajar KSK</title><style>body{text-align: center;align: center;}</style></head><body >	<header class="site-he