<?php require_once('../Connections/local.php'); ?><?php//initialize the sessionif (!isset($_SESSION)) {  session_start();}date_default_timezone_set("Asia/Kuala_Lumpur");// ** Logout the current user. **$logoutAction = $_SERVER['PHP_SELF']."?doLogout=true";if ((isset($_SERVER['QUERY_STRING'])) && ($_SERVER['QUERY_STRING'] != "")){  $logoutAction .="&". htmlentities($_SERVER['QUERY_STRING']);}if ((isset($_GET['doLogout'])) &&($_GET['doLogout']=="true")){  //to fully log out a visitor we need to clear the session varialbles  $_SESSION['MM_Username'] = NULL;  $_SESSION['MM_UserGroup'] = NULL;  $_SESSION['PrevUrl'] = NULL;  unset($_SESSION['MM_Username']);  unset($_SESSION['MM_UserGroup']);  unset($_SESSION['PrevUrl']);	  $logoutGoTo = "../logout.php";  if ($logoutGoTo) {    header("Location: $logoutGoTo");    exit;  }}?><?phpif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}$colname_senarai_pelajar = "-1";if (isset($_GET['filter_year'])) {  $colname_senarai_pelajar = $_GET['filter_year'];}mysql_select_db($database_local, $local);$query_senarai_pelajar = sprintf("SELECT * FROM pelajar WHERE tahun = %s ORDER BY no_matrik ASC", GetSQLValueString($colname_senarai_pelajar, "int"));$senarai_pelajar = mysql_query($query_senarai_pelajar, $local) or die(mysql_error());$row_senarai_pelajar = mysql_fetch_assoc($senarai_pelajar);$totalRows_senarai_pelajar = mysql_num_rows($senarai_pelajar);?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><script type = "text/javascript" src="../script.js"></script><link rel="stylesheet" href="../css/table.css"><link rel="stylesheet" href="../css/sidebar.css"><link rel="stylesheet" href="../css/header.css"><link rel="stylesheet" href="../css/register.css"><link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"><title>Sistem Kehadiran Pelajar KSK</title><style>	body{	text-align: center;	align: center;	margin: 0;	  background:#555;	  font-family: sans-serif;	  font-weight: 100;	}</style></head><body >	<header class="site-header">        <div id="mySidenav" class="sidenav">          <a href="javascript:void(0)" class="closebtn" onclick="closeNav()">&times;</a>          <a href="semakkehadiran.php"><i class="fa fa-search" aria-hidden="true"></i>Semak Kehadiran</a>          <a href="daftarpelajar.php"><i class="fa fa-user-plus" aria-hidden="true"></i>Daftar Masuk Pelajar</a>          <a href="javascript:void(0)" onclick="closeNav()"><i class="fa fa-users" aria-hidden="true"></i>Senarai Pelajar</a>          <a href="<?php echo $logoutAction ?>" style="color:#FFF"><i class="fa fa-sign-out"></i>Log Keluar</a>        </div>      <div class="site-identity">      	<span style="font-size:30px;cursor:pointer; color:#FFFFFF" onclick="openNav()">&#9776;</span>                <h1><a href="#">Sistem Kehadiran Pe