<?php require_once('../Connections/local.php'); ?><?php//initialize the sessionif (!isset($_SESSION)) {  session_start();}date_default_timezone_set("Asia/Kuala_Lumpur");// ** Logout the current user. **$logoutAction = $_SERVER['PHP_SELF']."?doLogout=true";if ((isset($_SERVER['QUERY_STRING'])) && ($_SERVER['QUERY_STRING'] != "")){  $logoutAction .="&". htmlentities($_SERVER['QUERY_STRING']);}if ((isset($_GET['doLogout'])) &&($_GET['doLogout']=="true")){  //to fully log out a visitor we need to clear the session varialbles  $_SESSION['MM_Username'] = NULL;  $_SESSION['MM_UserGroup'] = NULL;  $_SESSION['PrevUrl'] = NULL;  unset($_SESSION['MM_Username']);  unset($_SESSION['MM_UserGroup']);  unset($_SESSION['PrevUrl']);	  $logoutGoTo = "../logout.php";  if ($logoutGoTo) {    header("Location: $logoutGoTo");    exit;  }}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><script type = "text/javascript" src="../script.js"></script><link rel="stylesheet" href="../css/sidebar.css"><link rel="stylesheet" href="../css/header.css"><link rel="stylesheet" href="../css/register.css"><title>Sistem Kehadiran Pelajar KSK</title><style>body{text-align: center;align: center;}</style></head><body >	<header class="site-header">        <div id="mySidenav" class="sidenav">          <a href="javascript:void(0)" class="closebtn" onclick="closeNav()">&times;</a>          <a href="semakkehadiran.php">Semak Kehadiran</a>          <a href="daftarpelajar.php">Daftar Masuk Pelajar</a>          <a href="javascript:void(0)" onclick="closeNav()">Senarai Pelajar</a>          <a href="<?php echo $logoutAction ?>">Log Keluar</a>        </div>      <div class="site-identity">      	<span style="font-size:30px;cursor:pointer; color:#FFFFFF" onclick="openNav()">&