<?php require_once('Connections/localhost.php'); ?><?phpif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}?><?php// *** Validate request to login to this site.if (!isset($_SESSION)) {  session_start();}$loginFormAction = $_SERVER['PHP_SELF'];if (isset($_GET['accesscheck'])) {  $_SESSION['PrevUrl'] = $_GET['accesscheck'];}if (isset($_POST['matric_no'])) {  $loginUsername=$_POST['matric_no'];  $password=$_POST['password'];  $MM_fldUserAuthorization = "";  $MM_redirectLoginSuccess = "Templates/isikehadiran.php";  $MM_redirectLoginFailed = "Templates/LamanUtama.php";  $MM_redirecttoReferrer = false;  mysql_select_db($database_localhost, $localhost);    $LoginRS__query=sprintf("SELECT no_matrik, password FROM nama_pelajar WHERE no_matrik=%s AND password=%s",    GetSQLValueString($loginUsername, "text"), GetSQLValueString($password, "text"));      $LoginRS = mysql_query($LoginRS__query, $localhost) or die(mysql_error());  $loginFoundUser = mysql_num_rows($LoginRS);  if ($loginFoundUser) {     $loginStrGroup = "";    	if (PHP_VERSION >= 5.1) {session_regenerate_id(true);} else {session_regenerate_id();}    //declare two session variables and assign them    $_SESSION['MM_Username'] = $loginUsername;    $_SESSION['MM_UserGroup'] = $loginStrGroup;	          if (isset($_SESSION['PrevUrl']) && false) {      $MM_redirectLoginSuccess = $_SESSION['PrevUrl'];	    }    header("Location: " . $MM_redirectLoginSuccess );	$_SESSION['matric_no'] = $loginUsername;  }  else {    echo '<script>alert("No IC/Kata Laluan salah")</script>';  }}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><title>Sistem Kehadiran Pelajar KSK</title><style>	body {	  margin: 0;	  background:#555;	  font-family: sans-serif;	  font-weight: 100;	}		a {	  text-decoration: none;	  color: #000;	}	.site-header { 		background: linear-gradient(45deg, #49a09d, #5f2c82);	  border-bottom: 1px solid #ccc;	  padding: .5em 1em;	}		.site-header::after {	  content: "";	  display: table;	  clear: both;	}		.site-identity {	  float: left;	}		.site-identity h1 {	  font-size: 1.5em;	  margin: .7em 0 .3em 0;	  display: inline-block;	}		.site-identity img {	  max-width: 299px;	  float: left;	  margin: 0 10px 0 0;	}	.site-navigation {	  float: right;	  padding-right: 30px;	}		.site-navigation ul, li {	  margin: 0; 	  padding: 0;	  color:#FFF;	}		.site-navigation li {	  display: inline-block;	  margin: 1.4em 1em 1em 1em;	}		.site-navigation h1, li  a{	  color:#FFF;	}	.site-navigation h1, h1  a{	  color:#FFF;	}</style></head><body>	<header class="site-header">      <div class="site-identity">        <a href="#"><img src="logo.png" width="288" height="93" alt="" /></a>        <h1><a href="#">Sistem Kehadiran Pelajar KSK</a></h1>      </div>        <nav class="site-navigation">        <ul class="n