<?php require_once('Connections/local.php'); ?><?phpif (!function_exists("GetSQLValueString")) {function GetSQLValueString($theValue, $theType, $theDefinedValue = "", $theNotDefinedValue = "") {  if (PHP_VERSION < 6) {    $theValue = get_magic_quotes_gpc() ? stripslashes($theValue) : $theValue;  }  $theValue = function_exists("mysql_real_escape_string") ? mysql_real_escape_string($theValue) : mysql_escape_string($theValue);  switch ($theType) {    case "text":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;        case "long":    case "int":      $theValue = ($theValue != "") ? intval($theValue) : "NULL";      break;    case "double":      $theValue = ($theValue != "") ? doubleval($theValue) : "NULL";      break;    case "date":      $theValue = ($theValue != "") ? "'" . $theValue . "'" : "NULL";      break;    case "defined":      $theValue = ($theValue != "") ? $theDefinedValue : $theNotDefinedValue;      break;  }  return $theValue;}}?><?php// *** Validate request to login to this site.if (!isset($_SESSION)) {  session_start();  }if (isset($_SESSION['matric_no'])){	unset($_SESSION['matric_no']);}$loginFormAction = $_SERVER['PHP_SELF'];if (isset($_GET['accesscheck'])) {  $_SESSION['PrevUrl'] = $_GET['accesscheck'];}if (isset($_POST['no_matrik'])) {  $loginUsername=$_POST['no_matrik'];  $password=$_POST['password'];  $MM_fldUserAuthorization = "";  $MM_redirectLoginSuccess = "Templates/isikehadiran.php";  $MM_redirectLoginFailed = "Templates/semakkehadiran.php";  $MM_redirecttoReferrer = false;  mysql_select_db($database_local, $local);    $LoginRS__query=sprintf("SELECT no_matrik, kata_laluan FROM pelajar WHERE no_matrik=%s AND kata_laluan=%s",    GetSQLValueString($loginUsername, "text"), GetSQLValueString($password, "text"));      $LoginRS = mysql_query($LoginRS__query, $local) or die(mysql_error());  $loginFoundUser = mysql_num_rows($LoginRS);  if ($loginFoundUser) {     $loginStrGroup = "";    	if (PHP_VERSION >= 5.1) {session_regenerate_id(true);} else {session_regenerate_id();}    //declare two session variables and assign them    $_SESSION['MM_Username'] = $loginUsername;    $_SESSION['MM_UserGroup'] = $loginStrGroup;	 	$_SESSION['matric_no'] = $loginUsername;         if (isset($_SESSION['PrevUrl']) && false) {      $MM_redirectLoginSuccess = $_SESSION['PrevUrl'];	    }    header("Location: " . $MM_redirectLoginSuccess );  }  else {    echo '<script>alert("No IC/Kata Laluan salah")</script>';  }}error_reporting(0);?><!DOCTYPE html><html lang="en" ><head>  <meta charset="UTF-8">  <title>HTML5 Login Form with validation Example</title>  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/normalize/5.0.0/normalize.min.css"><link rel="stylesheet" href="css/header.css"><link rel="stylesheet" href="css/login.css"></head><body>	<header class="site-header">      <div class="site-identity">        <h1><a href="#">Sistem Kehadiran Pelajar KSK</a></h1>      </div>        <nav class="site-navigation">        <ul class="nav">          <li><a href="pelajar.php">Pelajar</a></li>           <li><a href="guru.php">Guru</a></li>         </ul>      </nav>    </header>    <!-- partial:index.partial.html --><div id="login-form-wrap">  <h2>Log Masuk	Pelajar</h2>  <form ACTION="<?php echo $loginFormAction; ?>" METHOD="POST" id="login-form">    <p>    <input name="no_matrik" value="<?php echo $_SESSION['matric_no']; ?>" type="text" required id="no_matrik" placeholder="No Matrik">    	<i class="validation"><span></span><span></span></i>    </p>    <p>    <input type="password" id="password" name="password" placeholder="Kata Laluan" required><i class="validation"><span></span><span></span></i>    </p>    <p>    <input type="submit" id="login" value="Login">    </p>  </form>  <div id="create-account-wrap">	<p>Anda seorang guru? <a href="guru.php">Klik disini</a></p>  </div><!--